et the part between GET and HTTP\r\n        clean_log.append(line[line.index(\"GET\")+4:line.index(\"HTTP\")])\r\n    except:\r\n        pass\r\n\r\ncounter = collections.Counter(clean_log)\r\n\r\n# get the Top 50 most popular URLs\r\nfor count in counter.most_common(50):\r\n    print(str(count[1]) + \"\\t\" + str(count[0]))\r\n\r\nlogfile.close()'),(1308,750,'_wpcom_is_markdown','1'),(1305,747,'_wpcom_is_markdown','1'),(1306,748,'_wpcom_is_markdown','1'),(1307,749,'_wpcom_is_markdown','1'),(1309,751,'_wpcom_is_markdown','1'),(1310,752,'_wpcom_is_markdown','1'),(1311,753,'_wpcom_is_markdown','1'),(1312,754,'_wpcom_is_markdown','1'),(1302,745,'_wpcom_is_markdown','1'),(1303,735,'_wpcom_is_markdown','1'),(1304,746,'_wpcom_is_markdown','1'),(1314,755,'_wpcom_is_markdown','1'),(1316,756,'_wpcom_is_markdown','1'),(1317,757,'_wpcom_is_markdown','1'),(1318,758,'_wpcom_is_markdown','1'),(1319,759,'_wpcom_is_markdown','1'),(1320,760,'_wpcom_is_markdown','1'),(1321,761,'_wpcom_is_markdown','1'),(1322,762,'_wpcom_is_markdown','1'),(1323,763,'_wpcom_is_markdown','1'),(1324,764,'_wpcom_is_markdown','1'),(1325,765,'_wpcom_is_markdown','1'),(1326,766,'_wpcom_is_markdown','1'),(1327,767,'_wpcom_is_markdown','1'),(1328,768,'_wpcom_is_markdown','1'),(1329,769,'_wpcom_is_markdown','1'),(1330,770,'_wpcom_is_markdown','1'),(1334,772,'_wpcom_is_markdown','1'),(1333,771,'_wpcom_is_markdown','1'),(1335,773,'_wpcom_is_markdown','1'),(1336,774,'_wpcom_is_markdown','1'),(1337,775,'_wpcom_is_markdown','1'),(1338,776,'_wpcom_is_markdown','1'),(1339,777,'_wpcom_is_markdown','1'),(1341,779,'_wpcom_is_markdown','1'),(1342,780,'_wpcom_is_markdown','1'),(1343,696,'_wpcom_is_markdown','1'),(1340,778,'_wpcom_is_markdown','1'),(1344,781,'_wpcom_is_markdown','1'),(1345,782,'_wpcom_is_markdown','1'),(1489,367,'post_views_count','0'),(1455,711,'post_views_count','2'),(1457,702,'post_views_count','0'),(1493,341,'post_views_count','0'),(1462,569,'post_views_count','0'),(1464,547,'post_views_count','0'),(146